mapper.select:
SELECT base.c_app_no as applicationNo, base.c_ply_no as policyNo, base.c_prod_no as riskCode, base.c_dpt_cde as insurerCode, '泰山保险' as insurerName, 'NORMAL_CHANNEL' as insuranceChannels, 'false' as internet, decode(base.c_prod_no,'0320','02','01') as insuranceType, 'VEHICLE' as type, '' as agentName, '' as agencyCode, (SELECT cha.c_cha_nme FROM WEB_CUS_CHA cha WHERE cha.c_cha_cde = base.c_brkr_cde) as agencyName, '' as superviseOrgCode, (SELECT cha.c_cha_nme FROM WEB_CUS_CHA cha WHERE cha.c_cha_cde = base.c_brkr_cde) as superviseOrgName, base.n_prm as grossPremium, vat.n_sum_price as netPremium, vat.n_tax_amt_y as taxAmount, to_char(base.t_crt_tm,'yyyy-mm-dd hh24:mi:ss') as signDate, to_char(base.t_insrnc_bgn_tm,'yyyy-mm-dd hh24:mi:ss') as insurerStartTime, to_char(base.t_insrnc_end_tm,'yyyy-mm-dd hh24:mi:ss') as insurerEndTime, (SELECT tax.n_taxable_amt FROM web_ply_vs_tax tax WHERE tax.c_app_no = base.c_app_no) as vehicleAndVesselTax, decode(base.c_ply_sts,'I','01','T','03','99') as status, to_char(base.t_insrnc_bgn_tm,'yyyy-mm-dd')||'至'||to_char(base.t_insrnc_end_tm,'yyyy-mm-dd') as insuredPeriod, vhl.c_plate_no as plateNo, vhl.c_eng_no as engineNo, vhl.c_frm_no as vehicleIdentificationNo, '0' as agricultureFlag, vhl.c_model_nme as brandNo FROM web_ply_base base, web_org_sales sales,web_ply_vhl vhl,web_ply_vat_base vat WHERE sales.c_sls_cde = base.c_sls_cde and vat.c_app_no = base.c_app_no and base.c_app_no = vhl.c_app_no and base.c_prod_no like '03%' and (base.c_issue_src <> '6' or base.c_issue_src is null) and base.c_app_typ = 'A' and base.c_app_no = #{appNo}
and base.t_crt_tm between #{startDate} and #{endDate}
and base.c_brkr_cde in (select broker.c_brkr_cde from web_trd_broker_info broker where broker.c_id = 'D5FE81B37000764BE0530100007FD60B')
